import React from "react";
import { useAppSelector } from "../hook/hooks";
import { RootState } from "../store/store";

const AuthStatus: React.FC = () => {
  const { id, email, token } = useAppSelector((state: RootState) => state.auth);

  return {
    isAuth: !!email,
    email,
    token,
    id,
  };
};
