import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { fetchProducts } from "./productsSlice"; // Import the thunk
import { ProductProps } from "../../types/data"; // Assuming this exists

const ProductsList: React.FC = () => {
  const dispatch = useDispatch();
  const products = useSelector((state: any) => state.products.products);
  const loading = useSelector((state: any) => state.products.loading);
  const error = useSelector((state: any) => state.products.error);

  useEffect(() => {
    dispatch(fetchProducts()); // Dispatch the thunk on component mount
  }, [dispatch]); // Add the dispatch dependency

  const handleDeleteProduct = async (productId: number) => {
    try {
      await dispatch(deleteProduct(productId)); // Dispatch the thunk for deletion
    } catch (err) {
      console.error("Failed to delete product:", err);
    }
  };

  return (
    <div>
      {loading ? (
        <p>Loading products...</p>
      ) : error ? (
        <p>Error: {error}</p>
      ) : (
        <ul>
          {products.map((product: ProductProps) => (
            <li key={product.id}>
              {product.name} ({product.price})
              <button onClick={() => handleDeleteProduct(product.id)}>
                Delete
              </button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default ProductsList;
